# Download image from the database
def download_image(table_name="image"):
    if listbox_images.curselection():
        selected_image_name = listbox_images.get(listbox_images.curselection())
        image_data = fetch_image_data_by_name(selected_image_name, user_session.user_id, table_name)

        if image_data:
            file_path = filedialog.asksaveasfilename(
                defaultextension=".png",
                filetypes=[("PNG files", ".png"), ("JPEG files", ".jpg"), ("All files", ".*")],
                title="Save Image As",
                initialfile=selected_image_name
            )
            
            if file_path:
                try:
                    with open(file_path, "wb") as f:
                        f.write(image_data)
                    messagebox.showinfo("Download Successful", f"Image downloaded to: {file_path}")
                except Exception as e:
                    messagebox.showerror("Download Failed", f"Error occurred while saving the image: {e}")
        else:
            messagebox.showwarning("Warning", "Image not found.")
    else:
        messagebox.showwarning("Warning", "No image selected.")

