def zoom_image(event):
    global scale_value
    if event.delta > 0:  # Zoom in
        scale_value = min(scale_value + 0.1, 1.0)
    else:  # Zoom out
        scale_value = max(scale_value - 0.1, 0.1)

    # Check if an image is selected in the regular images list
    if listbox_images.curselection():  
        selected_image_name = listbox_images.get(listbox_images.curselection())
        image_data = fetch_image_data_by_name(selected_image_name, user_session.user_id)
    # Check if an image is selected in the edited images list
    elif listbox_edited_images.curselection():  
        selected_image_name = listbox_edited_images.get(listbox_edited_images.curselection())
        image_data = fetch_edited_image_data_by_name(selected_image_name, user_session.user_id)
    else:
        return  # No image selected, exit the function

    if image_data:
        show_image(image_data)  # Use the fetched image data


