selected_image_path = None  # Biến toàn cục để lưu đường dẫn ảnh

def addimage():
    global selected_image_path
    selected_image_path = filedialog.askopenfilename(
        title="Select an Image",
        filetypes=[("Image Files", "*.png;*.jpg;*.jpeg;*.gif")]
    )
    if selected_image_path:
        # Nếu có ảnh được chọn, thực hiện hành động khác nếu cần
        window.destroy()
        import Function
        Function.create_menu_window(selected_image_path)
    else:
        print("No image selected.")

